package LogicalProgram;

public class Swapping {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=15;
		int b=20;
		
		a=a+b; //35
		b=a-b; //15
		a=a-b; //20
		System.out.println("value of a: "+ a);
		System.out.println("value of b: "+ b);
	}

}
