package LogicalProgram;

public class SwappingWithThirdVariable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b=20;
		int c=a;
			a=b;
			b=c;
			System.out.println("Value of a:-"+ a);
			System.out.println("Value of b:-"+ b);
	}

}
